// app/(tabs)/(home)/index.tsx
import React from "react";
import { FlatList, StyleSheet, View } from "react-native";
import { useTheme } from "@react-navigation/native";
import { DemoCard } from "@/components/DemoCard";
import { drills } from "../../data/drills"; // Import drills data

interface DemoCardData {
  id: string;
  title: string;
  description: string;
  route: string;
  color?: string; // Optional color for the icon
}

export default function HomeScreen() {
  const theme = useTheme();

  // Convert drills data to DemoCardData
  const demoCardData: DemoCardData[] = drills.map((drill) => ({
    id: drill.id,
    title: drill.name,
    description: drill.description || "No description available", // Provide a default description
    route: `/drill/${drill.id}`,
    color: "#007AFF", // You can customize the color for each drill
  }));

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <FlatList
        data={demoCardData}
        renderItem={({ item }) => <DemoCard item={item} />}
        keyExtractor={(item) => item.id} // Use drill id as key
        contentContainerStyle={styles.listContainer}
        contentInsetAdjustmentBehavior="automatic"
        showsVerticalScrollIndicator={false}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  listContainer: {
    paddingTop: 48,
    paddingHorizontal: 16,
    paddingBottom: 100, // Extra padding for floating tab bar
  },
});
```

